var lang=localStorage.getItem("lang")||"",getHots=function(){return axios.get($baseUrl+"/news/getHotNews")},autoHeight=function(){var e=$("body").height(),t=$(".footer").innerHeight();$(".main").height(e-t)},render=function(e,t){$(e).html(""),$(e).append(t)},renderHotNews=function(){getHots().then(function(e){var t=e.data.data;if(t){var n="";switch(t.type){case"中心动态":n="news-article";break;case"图片新闻":n="pic-news-article";break;case"通知公告":n="notice-article";break;case"行业资讯":n="infos-article"}var a='<a href="'+("en"===lang?"en":"")+"/page/news/"+n+"/?id="+t.id+'">\n      <span class="hot">'+t.title+"</span>\n    </a>";render("#hotNews",a)}})};$(function(){autoHeight(),"index"===$("body")[0].className&&renderHotNews(),$(window).resize(function(){autoHeight()})});