INSERT INTO `news` (`id`, `issuer`, `title`, `tabloid`, `content`, `type`, `url`, `image`, `page_view`, `publish_date`, `update_date`) VALUES
(1, 'mihua', '郑州大学第一附属医院赵杰教授团队博士后招聘启事', NULL, '<div class=\"newsCon\"><p><span style=\"font-size: 16px\">　　赵杰教授团队主要从事互联网医疗、远程医疗与医疗健康大数据的系统研究和转化应用，是我国该领域的主要科研团队之一。团队拥有建筑面积7000余平方米的独立科研场地、全国领先的医疗健康大数据分析支撑平台和广泛的科研协同网络，与华为技术有限公司、中国移动集团有限公司、中国信息通信研究院等知名科研院所、企业和大型医疗机构建立有密切的合作关系，团队已聚集了一批毕业于国内外名校的教授、博士（后）、硕士等各层次人才，协同科研能力突出。</span></p>\\n<p><span style=\"font-size: 16px\">　　该团队牵头建设互联网医疗系统与应用国家工程实验室、移动医疗技术与服务国家地方联合工程实验室、国家医疗数据中心河南分中心等多个国家级科研及数据平台，多次实现河南省医疗卫生领域重大科技项目、国家级科技平台等的“零”突破，在国内外远程医疗、医疗健康大数据等领域具有重要影响。团队承担河南省人民政府十项重点民生工程、河南省委省政府重点民生实事工程等项目，取得良好社会经济效益。承担国家卫生信息标准研制、国家重点研发计划“精准医学研究”重点专项（基于远程/移动医疗网络的精准医疗综合服务示范体系建设与推广，中央财政经费3981万元）、国家科技惠民计划专项、中央引导地方科技发展专项、河南省重大科技专项、国家自然科学基金等国家和省部级项目21项，获批科研经费累计1亿余元。</span></p>\\n<p><span style=\"font-size: 16px\">　　学科带头人赵杰教授是二级教授、国务院特殊津贴专家、全国五一劳动奖章获得者、国家卫生计生委中青年突出贡献专家，国家重点研发计划专项、国家自然科学基金等项目评审专家。现任郑州大学第一附属医院党委副书记，国家远程医疗中心主任、互联网医疗系统与应用国家工程实验室主任、河南省数字医疗工程技术研究中心主任等职。兼任中国卫生信息与健康医疗大数据学会远程医疗信息化专业委员会主任委员、中华医学会临床药学分会主任委员、中国卫生健康互联网+远程医疗联盟常务副理事长等学会职务。长期致力于健康医疗信息化工程、远程与互联网医疗、医疗大数据与精准医疗等领域的研究与实践工作。主持编制国家卫生行业标准2项、参与3项，出版学术专著和教材16部，获国家软件著作权32项，申报国家发明专利16项。公开发表180余篇学术论文，参编《国家远程医疗信息系统建设技术指南（2014）》1部，先后获得中华医学科技进步一等奖、河南省科技进步一等奖等省部级以上奖励7项。</span></p>\\n<p><span style=\"font-size: 16px\">　　因科研和临床转化工作需要，赵杰教授团队依托互联网医疗系统与应用国家工程实验室、国家远程医疗中心等平台，诚招有志于互联网医疗、远程/移动医疗、医疗大数据、人工智能、精准医疗等研究领域的博士后6名（2018年），旨在通过“高标准、严要求、强保障”的博士后训练和国家重大科技项目的实施，培养一批在国内外有影响的高层次、高水平的科研领军人才。</span></p>\\n<p><span style=\"font-size: 16px\"><strong>　　一、主要研究方向：</strong></span></p>\\n<ul style=\"margin-left: 40px\">\\n<li>\\n<span style=\"font-size: 16px\">医疗健康大数据分析与临床应用；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">医疗可穿戴设备研发与临床应用；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">面向精准医疗服务的医疗信息系统开发与转化；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">医疗人工智能与前沿技术研发应用。</span></li>\\n</ul>\\n<p><span style=\"font-size: 16px\"><strong>　　二、博士后岗位应聘要求</strong></span></p>\\n<ul style=\"margin-left: 40px\">\\n<li>\\n<span style=\"font-size: 16px\">具有严谨的科研态度，扎实的训练基础和良好的团队合作精神；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">已取得博士学位，具有较强的英语阅读和写作能力；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">有医学信息学、医学统计学、计算机应用、互联网医疗、人工智能、医疗大数据等背景的博士毕业生；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">博士期间以第一作者发表高影响因子SCI论文。</span></li>\\n</ul>\\n<p><span style=\"font-size: 16px\"><strong>　　三、聘期待遇</strong></span></p>\\n<ul style=\"margin-left: 40px\">\\n<li>\\n<span style=\"font-size: 16px\">医院发放薪酬20万/年，缴纳社会保险、住房公积金；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">提供免费的两室一厅博士后公寓；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">科室给予适当奖励；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">科研项目奖励：申请取得各级科研项目，1：1匹配研究经费；省级科研项目，以获得经费的10%作为个人生活补助；国家级科研项目，以获得经费20%作为个人生活补助；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">科研论文奖励：依据医院奖励政策给予奖励，并匹配相应科研经费。发表SCI论文第一作者，影响因子＜5分者，每分1万；影响因子≥5分、＜10分者，每分2万；影响因子≥10分者，每分3万，并匹配一定科研经费；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">期间获得国家发明专利、取得省级、国家科技进步奖，医院均给予重奖；</span></li>\\n<li>\\n<span style=\"font-size: 16px\">其他：专职博士后在站期间可申报学校专业技术职务评聘。专职博士后聘期结束后，优秀者可留院工作或申请续聘。</span></li>\\n</ul>\\n<p><span style=\"font-size: 16px\"><strong>　　四、简历投递</strong></span></p>\\n<p><span style=\"font-size: 16px\">　　有意者简历发至<a href=\"mailto:zhaiyunkai@163.com\">zhaiyunkai@163.com</a>，命名为：姓名＿郑州大学第一附属医院赵杰教授团队博士后申请。</span></p>\\n<p><span style=\"font-size: 16px\">　　联系人：翟运开 教授    15037129939</span></p>\\n<p><span style=\"font-size: 16px\">　　<strong>欢迎前来互联网医疗系统与应用国家工程实验室、国家远程医疗中心考察！</strong></span></p>\\n<p><span style=\"font-size: 16px\"> </span></p>\\n<p><span style=\"font-size: 16px\">                                                  2018年6月21日</span></p>\\n</div>', '通知公告', '--', NULL, 0, '2018-06-21 14:46:55', '2018-06-21 14:46:55'),
(2, 'mihua', '关于召开河南省科技惠民计划专项示范点建设工作推进会的通知', NULL, '<div class=\"newsCon\"><p align=\"center\"> </p>\\n<p align=\"center\"><img alt=\"\" src=\"/u/cms/www/201806/11143622rnb0.Jpeg\" style=\"width: 750px; height: 1061px;\"/><img alt=\"\" src=\"/u/cms/www/201806/11130403k61s.Jpeg\" style=\"width: 750px; height: 1061px;\"/></p>\\n</div>', '通知公告', '--', NULL, 0, '2018-06-11 12:15:09', '2018-06-11 12:15:09'),
(3, 'mihua', '关于召开《河南省远程医疗管理办法（征求意见稿）》视频研讨会的通知', NULL, '<div class=\"newsCon\"><p><img alt=\"\" src=\"/u/cms/www/201806/02084622d2kk.jpg\" style=\"width: 635px; height: 776px;\"/></p>\\n<p><img alt=\"\" src=\"/u/cms/www/201806/02084653019y.jpg\" style=\"width: 635px; height: 642px;\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201806/02084622d2kk.jpg', 0, '2018-06-01 16:25:31', '2018-06-01 16:25:31'),
(4, 'mihua', '关于组织参加国家远程医疗中心揭牌暨河南省远程医疗工作推进会的通知', NULL, '<div class=\"newsCon\"><p style=\"text-align: center\"><img alt=\"\" src=\"/u/cms/www/201805/24181638g2am.jpg\" style=\"height: 1061px; width: 750px\"/><img alt=\"\" src=\"/u/cms/www/201805/2418170888n3.jpg\" style=\"height: 1061px; width: 750px\"/><img alt=\"\" src=\"/u/cms/www/201805/24181724kn1m.jpg\" style=\"height: 1061px; width: 750px\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201805/24181638g2am.jpg', 0, '2018-05-24 18:16:50', '2018-05-24 18:16:50'),
(5, 'mihua', '关于召开“中国卫生健康互联网+远程医疗联盟”成立、国家远程医疗中心揭牌仪式的通知', NULL, '<div class=\"newsCon\"><p style=\"text-align: center\"><img alt=\"\" src=\"/u/cms/www/201805/231827083plw.jpg\" style=\"height: 1060px; width: 750px\"/><img alt=\"\" src=\"/u/cms/www/201805/2318272087ls.jpg\" style=\"height: 1060px; width: 750px\"/><img alt=\"\" src=\"/u/cms/www/201805/2318273117wh.jpg\" style=\"height: 1060px; width: 750px\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201805/231827083plw.jpg', 0, '2018-05-23 18:25:03', '2018-05-23 18:25:03'),
(6, 'mihua', '关于征集“中国卫生健康互联网+远程医疗联盟”成员单位的通知', NULL, '<div class=\"newsCon\"><p style=\"text-align: center\"><img alt=\"\" src=\"/u/cms/www/201805/23182309rmvm.jpg\" style=\"height: 1060px; width: 750px\"/><img alt=\"\" src=\"/u/cms/www/201805/23182319aowk.jpg\" style=\"height: 1060px; width: 750px\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201805/23182309rmvm.jpg', 0, '2018-05-23 18:24:20', '2018-05-23 18:24:20'),
(7, 'mihua', '关于征集“中国卫生健康互联网+远程医疗联盟”成员单位的预通知', NULL, '<div class=\"newsCon\"><p style=\"text-align: center\"><img alt=\"\" src=\"/u/cms/www/201805/22163424javu.jpg\" style=\"height: 1061px; width: 750px\"/><img alt=\"\" src=\"/u/cms/www/201805/221809005ga5.jpg\" style=\"height: 1061px; width: 750px\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201805/22163424javu.jpg', 0, '2018-05-22 16:36:16', '2018-05-22 16:36:16'),
(8, 'mihua', '关于召开“中国卫生健康互联网+远程医疗联盟”成立仪式的预通知', NULL, '<div class=\"newsCon\"><h4 style=\"text-align: justify\">\\r\\n	 </h4>\\n<p style=\"text-align: center\"><strong><span style=\"font-size: 16px\">关于召开“中国卫生健康互联网+远程医疗联盟”成立、国家远程医疗中心揭牌暨河南省远程医疗工作推进会的预通知</span></strong></p>\\n<p><strong><span style=\"font-size: 16px\">各有关单位：</span></strong></p>\\n<p><span style=\"font-size: 16px\">　　根据《中国卫生信息与健康医疗大数据学会章程》、《中国卫生信息与健康医疗大数据学会分支机构管理办法》的要求，定于2018年5月28日在河南省郑州市召开中国卫生信息与健康医疗大数据学会“中国卫生健康互联网+远程医疗联盟”成立会议。经国家卫生健康委员会医政医管局同意，依托郑州大学第一附属医院设立的国家远程医疗中心也将在联盟成立会议上举行揭牌仪式。</span></p>\\n<p><span style=\"font-size: 16px\">　　现将有关事宜通知如下：</span></p>\\n<p><strong><span style=\"font-size: 16px\">　　一、会议时间</span></strong></p>\\n<p><span style=\"font-size: 16px\">　　2018年5月28日  14:30-17:30</span></p>\\n<p><strong><span style=\"font-size: 16px\">　　二、会议地点</span></strong></p>\\n<p><span style=\"font-size: 16px\">　　郑州大学第一附属医院郑东院区球形会议厅（郑州市郑东新区北三环与龙湖中环路西南侧）</span></p>\\n<p><span style=\"font-size: 16px\">　<strong>　三、大会组织</strong></span></p>\\n<p><span style=\"font-size: 16px\">　　（一）主办单位</span></p>\\n<p><span style=\"font-size: 16px\">　　中国卫生信息与健康医疗大数据学会</span></p>\\n<p><span style=\"font-size: 16px\">　　国家远程医疗中心</span></p>\\n<p><span style=\"font-size: 16px\">　　（二）承办单位</span></p>\\n<p><span style=\"font-size: 16px\">　　郑州大学第一附属医院</span></p>\\n<p><strong><span style=\"font-size: 16px\">　　四、会议内容</span></strong></p>\\n<p><span style=\"font-size: 16px\">　　（一）成立“中国卫生健康互联网+远程医疗联盟”。</span></p>\\n<p><span style=\"font-size: 16px\">　　（二）宣布并聘任“中国卫生健康互联网+远程医疗联盟”第一届领导机构名单。</span></p>\\n<p><span style=\"font-size: 16px\">　　（三）部署“中国卫生健康互联网+远程医疗联盟”相关工作。</span></p>\\n<p><span style=\"font-size: 16px\">　　（四）举行国家远程医疗中心揭牌仪式。</span></p>\\n<p><span style=\"font-size: 16px\">　　（五）召开河南省远程医疗工作推进会。</span></p>\\n<p><strong><span style=\"font-size: 16px\">　　五、参会人员</span></strong></p>\\n<p><span style=\"font-size: 16px\">　　（一）国家卫生健康委员会相关领导</span></p>\\n<p><span style=\"font-size: 16px\">　　（二）国家工信部相关领导</span></p>\\n<p><span style=\"font-size: 16px\">　　（三）中国卫生信息与健康医疗大数据学会领导</span></p>\\n<p><span style=\"font-size: 16px\">　　（四）联盟成员单位</span></p>\\n<p><span style=\"font-size: 16px\">　　（五）学会远程医疗信息化专委会委员单位代表</span></p>\\n<p><span style=\"font-size: 16px\">　　（六）河南省人民政府、河南省卫生计生委、郑州大学第一附属医院及相关医疗机构</span></p>\\n<p><span style=\"font-size: 16px\">　　（七）其他相关单位</span></p>\\n<p><span style=\"font-size: 16px\">　<strong>　六、会议报名及食宿安排</strong></span></p>\\n<p><span style=\"font-size: 16px\">　　请参会人员填写参会回执（附件1）于5月25日中午12:00前发至邮箱<a href=\"mailto:telemedicine96299@163.com\">telemedicine96299@163.com</a>。</span></p>\\n<p><span style=\"font-size: 16px\">　　本次会议不收取任何费用。会议期间食宿统一安排，费用由主办方承担。交通费自理。</span></p>\\n<p><strong><span style=\"font-size: 16px\">　　七、联系方式</span></strong></p>\\n<p><span style=\"font-size: 16px\">　　远程医疗信息化专业委员会秘书处（郑州）：</span></p>\\n<p><span style=\"font-size: 16px\">　　李陈晨、马倩倩、卢耀恩  0371-67966210，18739925657、15136272621、18539982605</span></p>\\n<p><span style=\"font-size: 16px\">　　中国移动通信集团公司（北京）：武佳乐  13810572023</span></p>\\n<p><span style=\"font-size: 16px\">　　中国信息通信研究院（北京）:  冯天宜  18810626607</span></p>\\n<p><span style=\"font-size: 16px\"> </span></p>\\n<p><span style=\"font-size: 16px\">附件１：参会回执（中国卫生信息与健康医疗大数据学会互联网+远程医疗联盟成立暨国家远程医疗中心揭牌仪式）</span></p>\\n<p><span style=\"font-size: 16px\"> </span></p>\\n<p style=\"text-align: center\"><strong><span style=\"font-size: 16px\"><span style=\"font-size: 16px\">中国卫生信息与健康医疗大数据学会　　　　<span style=\"font-size: 16px\">国家远程医疗中心</span>　　　</span></span><span style=\"font-size: 16px\">郑州大学第一附属医院</span></strong></p>\\n<p><strong> </strong></p>\\n<p align=\"center\"><strong><span style=\"font-size: 16px\">二〇一八年五月十八日</span></strong></p>\\n</div>', '通知公告', '--', NULL, 0, '2018-05-22 16:20:13', '2018-05-22 16:20:13'),
(9, 'mihua', '关于确定河南省科技惠民计划专项社区示范点的通知', NULL, '<div class=\"newsCon\"><p style=\"text-align: center\"><img alt=\"\" src=\"/u/cms/www/201805/11091457lpf9.jpg\" style=\"height: 1053px; width: 750px\"/></p>\\n<p style=\"text-align: center\"><img alt=\"\" src=\"/u/cms/www/201805/11091511x0th.jpg\" style=\"height: 1053px; width: 750px\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201805/11091457lpf9.jpg', 0, '2018-05-11 09:15:31', '2018-05-11 09:15:31'),
(10, 'mihua', '关于招募河南省精神心理卫生联盟第二批联盟单位的通知', NULL, '<div class=\"newsCon\"><p align=\"left\">各联盟单位：</p>\\n<p align=\"left\">  为了深入贯彻全国卫生健康大会和习近平总书记关于加强心理健康服务的要求，依据《精神卫生法》《“健康中国2030”规划纲要》《“健康中原2030”规划纲要》和相关政策，为进一步落实国家卫生计生委等22部委提出的《关于加强心理健康服务的指导意见》，为加强河南省精神心理健康服务，提高各级专科医院、综合医院精神心理障碍的诊疗水平，规范心理治疗，我院本着“立足自建、自愿选择、责任分担、优势互补、互利共赢、资源共享、平台共用”的基本原则，联合河南省精神心理卫生医疗机构与相关专业，我科于2018年3月25日成功召开河南省精神心理卫生联盟成立大会，并进行首批联盟单位授牌仪式。现进行招募河南省精神心理卫生联盟第二批联盟单位。</p>\\n<p align=\"left\">一、参加人员</p>\\n<p align=\"left\">  联盟单位院长/业务院长/医务科长1人，各精神心理及相关专业主任。</p>\\n<p align=\"left\">二、参加事宜</p>\\n<p align=\"left\"> 1.签署《河南省精神心理联盟合作协议书》一式二份请签字盖章送交郑州大学第一附属医院。</p>\\n<p align=\"left\"> 2.联盟申请由郑州大学第一附属医院统一签字。</p>\\n<p align=\"left\"> 3.申请通过后发放《河南省精神心理卫生联盟》牌匾</p>\\n<p align=\"left\">三、联系方式</p>\\n<p align=\"left\"> 邮箱：<a href=\"mailto:jsxllm6831@163.com\">jsxllm6831@163.com</a></p>\\n<p align=\"left\"> 电话：13938221820  0371-67966831</p>\\n<p align=\"left\"> 联系人：王亚丽</p>\\n<p align=\"left\"> </p>\\n<p align=\"left\"> 附件一：关于河南省精神心理卫生联盟成立的通知</p>\\n<div> </div>\\n</div>', '通知公告', '--', NULL, 0, '2018-04-28 09:23:20', '2018-04-28 09:23:20'),
