(53, 'mihua', '关于举办2016年河南省远程医疗技能竞赛的通知', NULL, '<div class=\"newsCon\"><p style=\"text-align: center;\"><img alt=\"\" src=\"/u/cms/www/201609/191442149d6s.jpg\" style=\"width: 750px; height: 1061px;\"/></p>\\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/u/cms/www/201609/23171046wmqw.jpg\" style=\"width: 750px; height: 1061px;\"/></p>\\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/u/cms/www/201609/191442422dyu.jpg\" style=\"width: 750px; height: 1061px;\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201609/191442149d6s.jpg', 0, '2016-09-19 14:44:23', '2016-09-19 14:44:23'),
(54, 'mihua', '河南省卫生信息学会关于召开远程医疗信息化专业委员会第一届第二次全体委员会议及常务委员会议的通知', NULL, '<div class=\"newsCon\"><p> </p>\\n<p> </p>\\n<p style=\"text-align: center;\"> </p>\\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/u/cms/www/201609/20082230youk.jpg\" style=\"width: 750px; height: 1060px;\"/><img alt=\"\" src=\"/u/cms/www/201609/18160039xyhl.jpg\" style=\"width: 750px; height: 1060px;\"/></p>\\n</div>', '通知公告', '--', '/u/cms/www/201609/20082230youk.jpg', 0, '2016-09-18 16:01:37', '2016-09-18 16:01:37'),
